#include <iostream>
#include <vector>
#include <thread>

// Function to print the grid
void printGrid(const std::vector<std::vector<int>> &grid) {
    for (const auto &row : grid) {
        for (int cell : row) {
            std::cout << cell << " ";
        }
        std::cout << std::endl;
    }
    std::cout << std::endl;
}


